function handler_GPS_onPageShow(){
    // Get the location every 1 second
    if(!app.isNativeApp()) {
        app.alert('This App is running as a Web App, so device functions are not accessible');
        return;
    }
    debugger;
    // Update every 3 seconds
    var options = { maximumage: 3000, timeout: 5000, enableHighAccuracy: true, frequency: 1000 };
    _watchID = navigator.geolocation.watchPosition(_getGPS, undefined, options);
    _intervalCounter = 0;   
}

function handler_GPS_onPageLeft(){
    if(!app.isNativeApp()) {
        return;
    }        
	navigator.geolocation.clearWatch(_watchID);
}


