        return;
    }    
    navigator.accelerometer.clearWatch(_watchID);	
}


///////////////////// CONTACTS

function sortNames(a, b) {
    if(a.name.formatted===null || b.name.formatted===null) {
        return 0;
    }
    var x = a.name.formatted.toLowerCase();
    var y = b.name.formatted.toLowerCase();
    if(x<y) {
        return -1;
    } else {
        return 1;
    }
}

function contactsSuccess(contacts) {
    console.log('Success');
    contacts.sort(sortNames);
    console.log('Sorted');
    _contactsLoaded = true;
    // Copy sorted array of contacts into an object that includes divider field
    var dCont=[];
    var d;
    for (i=0, letter=''; i<contacts.length; i++) {
        // If this is a new letter, then add a divider element
        if(contacts[i].name.formatted!==null) {
            if(contacts[i].name.formatted.toUpperCase().charAt(0)!=letter) {
                d = {};
                letter = contacts[i].name.formatted.toUpperCase().charAt(0);
                d.label= letter;
                d.divider=true;
                dCont.push(d);
            }
            console.log(contacts[i].name.formatted);
            d = {};
            d.label = contacts[i].name.formatted;
            d.divider = false;
            dCont.push(d);
        }   
    } 
    var mapObjectRpt = { 
        'label':'label',   
        'divider':'divider'
    };
    app.populateWidget('lstCont', dCont, mapObjectRpt);     
    app.twirlyThing('lstCont', false);
}

function contactsFail(x) {
    _contactsLoaded = false;
    _myAlert('Fail');
}

function handler_Contacts_onPageShow(){
    // find all contacts     
    if(app.isNativeApp()) {
        var options = new ContactFindOptions();
        options.filter="*"; 
        options.multiple=true;
        var fields = ["name"];
        debugger;
        app.twirlyThing('lstCont', true);
        navigator.contacts.find(fields, contactsSuccess, contactsFail, {multiple:true});
    } else {
        app.alert('This App is running as a Web App, so device functions are not accessible');
    }    
}


